    }
  };

  /* Using hooks, check for contact name in the contacts array variable in props */
  useEffect(() => {
    const duplicateCheck = () => {
      const found = contacts.find((contact) => contact.name === name)
      if (found !== undefined) {
        return true;  
      } else {
        return false;
      }
    }
    if (duplicateCheck()) {
      setDuplicate(true);
    } else {
      setDuplicate(false);
    }
  }, [name, contacts, duplicate])

  return (
    <div>
      <section>
        <h2>Add Contact</h2> 
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
      </section>
    </div>
  );
};
